  name: "EmployeesTable",
  props: {
    employees: {
      type: Array,
      required: true
    }
  },
  data: () => ({
    headers: [
      { text: "Employee ID", value: "id" },
      { text: "Name", value: "name" },
      { text: "Position Title", value: "title" },
